import "../assets/css/galeria.css";

import { useContext } from "react";

import Context from "../context";
import Heart from "./Heart";

export default function Galeria() {
  const { fotos, setFavorite } = useContext(Context);

  /**
   * If the id is not in the favorite array, add it to the array.
   * @param id - the id of the item that is being added to the favorite list
   */
  const addToFavorite = (id) => {
    if (!favorite.includes(id)) setFavorite(favorite.concat(id));
    console.log(id);
  };

  return (
    <div className="galeria grid-columns-5 p-3">
      {fotos.map((foto, i) => (
        <div
          className="foto"
          style={{ backgroundImage: `url(${foto.src.medium})` }}
        >
          <Heart />
          <p className="fotoDescripcion"> {foto.alt} </p>
        </div>
      ))}
    </div>
  );
}
